function fadeScroll(e,t,o){const c=document.querySelector(e),i=c.querySelectorAll("div"),r=c.querySelector("#left"),l=c.querySelector("#right");let y=0;const n=i.length-1;r.addEventListener("click",()=>{i.forEach(e=>e.style.opacity=0),i[y].style.opacity=1,y<=0?y=n:y--}),l.addEventListener("click",()=>{i.forEach(e=>e.style.opacity=0),i[y].style.opacity=1,y<n?y++:y=0}),window.addEventListener("DOMContentLoaded",()=>{c.style.height=`${c.clientWidth*t/o}px`}),window.addEventListener("resize",()=>{c.style.height=`${c.clientWidth*t/o}px`}),document.addEventListener("keydown",e=>{"ArrowLeft"!==e.key&&"ArrowLeft"!==e.code||(i.forEach(e=>e.style.opacity=0),i[y].style.opacity=1,y<=0?y=n:y--),"ArrowRight"!==e.key&&"ArrowRight"!==e.code||(i.forEach(e=>e.style.opacity=0),i[y].style.opacity=1,y<n?y++:y=0)})}
