function fadeScroll(e,t,o){const l=document.querySelector(e),c=l.querySelectorAll("div"),i=l.querySelector("#left"),r=l.querySelector("#right");let y=0;const d=c.length-1;i.addEventListener("click",()=>{c.forEach(e=>e.style.opacity=0),c[y].style.opacity=1,y<=0?y=d:y--}),r.addEventListener("click",()=>{c.forEach(e=>e.style.opacity=0),c[y].style.opacity=1,y<d?y++:y=0}),window.addEventListener("DOMContentLoaded",()=>{l.style.height=`${l.clientWidth*t/o}px`}),window.addEventListener("resize",()=>{l.style.height=`${l.clientWidth*t/o}px`}),document.addEventListener("keydown",e=>{"ArrowLeft"!==e.key&&"ArrowLeft"!==e.code||(c.forEach(e=>e.style.opacity=0),c[y].style.opacity=1,y<=0?y=d:y--),"ArrowRight"!==e.key&&"ArrowRight"!==e.code||(c.forEach(e=>e.style.opacity=0),c[y].style.opacity=1,y<d?y++:y=0)}),1===c.length&&(i.style.display=r.style.display="none")}

fadeScroll("#levus-fadescroll",600,800);
